import React, { useState } from "react";
import ProductPopup from "./ProductPopup";
import Cart from "./Cart";

const App = () => {
  const [cart, setCart] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [isCartOpen, setIsCartOpen] = useState(false); // Toggle state for cart

  const addToCart = (product) => {
    if (!product) return;

    setCart((prevCart) => {
      const existingProduct = prevCart.find((item) => item.id === product.id);

      if (existingProduct) {
        return prevCart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });

    console.log("Cart Updated:", cart);
  };

  const handleProductClick = (product) => {
    setSelectedProduct(product);
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setSelectedProduct(null);
    setIsPopupOpen(false);
  };

  const toggleCart = () => {
    setIsCartOpen((prev) => !prev);
  };

  return (
    <div>
      {/* Cart Button */}
      <button onClick={toggleCart} className="cart-button">
        ðŸ›’ View Cart ({cart.length})
      </button>

      {/* Product Popup */}
      {isPopupOpen && (
        <ProductPopup product={selectedProduct} onClose={closePopup} onAddToCart={addToCart} />
      )}

      {/* Cart Sidebar */}
      <div className={`cart-sidebar ${isCartOpen ? "open" : ""}`}>
        <Cart cart={cart} onClose={toggleCart} />
      </div>

      {/* Add to Cart Button (Old Code Merged) */}
      {selectedProduct && (
        <button
          className="btn btn-primary"
          onClick={() => {
            addToCart(selectedProduct);
            closePopup();
          }}
        >
          Add to Cart
        </button>
      )}
    </div>
  );
};

export default App;
