import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "./components/Header";
import ItemCard from "./components/ItemCard";
import ProductPopup from "./components/ProductPopup";
import CartSidebar from "./components/CartSidebar";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

const API_URL = "https://interview.gdev.gosbfy.com/api/collections/Products/records";
const IMAGE_URL_TEMPLATE = "https://interview.gdev.gosbfy.com/api/files/";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [popupProduct, setPopupProduct] = useState(null);
  const [showCart, setShowCart] = useState(false);

  // Fetch products from API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(API_URL);
        const data = await response.json();
        console.log("API Response:", data); // Debug API Response

        if (data && data.items) {
          setProducts(
            data.items.map((item) => ({
              id: item.id,
              name: item.name || "Unknown Product",
              description: item.description || "No description available",
              price: item.price || 0,
              sku: item.sku || "N/A",
              categories: item.categories || ["None"],
              tags: item.tags || ["None"],
              image: `${IMAGE_URL_TEMPLATE}${item.collectionId}/${item.id}/${item.image}`,
            }))
          );
        } else {
          console.error("Invalid API response format:", data);
        }
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  // Handle clicking a product to show in popup
  const handleProductClick = (product) => {
    console.log("Product clicked:", product); // Debugging
    setPopupProduct(product);
  };

  // Add to cart function

  const addToCart = (product) => {
    console.log("Adding product to cart:", product);
    if (!product) return; // Prevent errors if product is undefined

    setCart((prevCart) => {
      const existingProduct = prevCart.find((item) => item.id === product.id);

      if (existingProduct) {
        // Increase quantity if product already in cart
        return prevCart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        // Add new product with quantity 1
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });

    // Update cart count based on the total number of items
    setCartCount((prevCount) => prevCount + 1);
  };

  return (
    <div>
      <Header cartCount={cart.reduce((total, item) => total + item.quantity, 0)} onCartClick={() => setShowCart(true)} />

      <div className="product-list d-flex flex-wrap gap-3 p-3">
        {products.length === 0 ? (
          <p>No products available. Please check the API.</p>
        ) : (
          products.map((product) => (
            <ItemCard key={product.id} product={product} onProductClick={handleProductClick} />
          ))
        )}
      </div>

      {popupProduct && (
        <ProductPopup
          key={popupProduct?.id}  // Force re-render when a new product is clicked
          product={popupProduct}
          onClose={() => setPopupProduct(null)}
          onAddToCart={addToCart}
        />
      )}

      {showCart && <CartSidebar cartItems={cart} onClose={() => setShowCart(false)} />}
    </div>
  );
}

export default App;
