import React from "react";
import "./ProductPopup.css";

const ProductPopup = ({ product, onClose }) => {
  if (!product) return null;

  console.log("Popup Product Data:", product); // ✅ Debugging

  return (
    <div className="popup-overlay">
      <div className="popup-content">
        <button className="close-btn" onClick={onClose}>×</button>

        {/* ✅ Fix: Ensure image loads correctly */}
        <img
          src={product.image || "https://via.placeholder.com/400"}
          alt={product.name || "No Name"}
          className="popup-image"
        />

        {/* ✅ Fix: Ensure correct product details are displayed */}
        <h2>{product.name || "No Name"}</h2>
        <p className="product-price">${product.price ? product.price.toFixed(2) : "0.00"}</p>
        <p>{product.description || "No description available"}</p>
      </div>
    </div>
  );
};

export default ProductPopup;
