import React from "react";

function ItemCard({ product, onProductClick })

 {
  const ItemCard = ({ product, onProductClick }) => {
  console.log("ItemCard received product:", product);
  
  return (
    
    <div
      className="card"
      style={{ width: "18rem", cursor: "pointer" }}
      onClick={() => onProductClick(product)}
    >
      <div className="item-card" onClick={() => onProductClick(product)}></div>
      <img
      
        src={product.image || "https://via.placeholder.com/200"}
        className="card-img-top"
        alt={product.name || "No Name"}
        style={{ height: "200px", objectFit: "cover" }}
      />
      <div className="card-body">
        <h5 className="card-title">{product.name || "No Name"}</h5>
        <p className="card-text">{product.description || "No description available"}</p>
        <p className="card-text fw-bold">${product.price?.toFixed(2) || "1.00"}</p>
      </div>
    </div>
  );
}
}

export default ItemCard;
