import React, { useState } from "react";
import "./ProductPopup.css";

const ProductPopup = ({ product, onClose, onAddToCart }) => {
  const [quantity, setQuantity] = useState(1);

  if (!product) return null; // Ensure product exists before rendering
  console.log("Popup Product Data:", product);

  // Construct the image URL properly or provide a placeholder
  const imageUrl = product.image
    ? `https://interview.gdev.gosbfy.com/api/files/${product.collectionId}/${product.id}/${product.image}`
    : "https://via.placeholder.com/300";

  // Ensure price is correctly formatted
  const price = product.price ? product.price.toFixed(2) : "0.00";

  // Handle quantity changes
  const handleQuantityChange = (type) => {
    setQuantity((prev) => (type === "increase" ? prev + 1 : Math.max(1, prev - 1)));
  };

  // Add to cart with the selected quantity
  const handleAddToCart = () => {
    if (typeof onAddToCart === "function") {
      onAddToCart({ ...product, quantity });
    } else {
      console.error("onAddToCart is not a function");
    }
    onClose();
  };

  return (
    <div className="popup-overlay">
      <div className="popup-container">
        {/* Close Button */}
        <button className="close-btn" onClick={onClose}>×</button>

        {/* Product Image */}
        <div className="popup-image-container">
          <img src={imageUrl} alt={product.name || "No Name"} className="popup-image" />
        </div>

        {/* Product Details */}
        <div className="popup-details">
          <h1 className="product-title">{product.name || "No Name Available"}</h1>
          <p className="product-price">${price}</p>
          <p className="product-availability">
            <strong>Availability:</strong> <span className="in-stock">In-stock</span>
          </p>
          <p className="product-description">{product.description || "No description available."}</p>

          {/* Quantity Selector & Add to Cart Button */}
          <div className="product-actions">
            <div className="quantity-container">
              <button onClick={() => handleQuantityChange("decrease")}>-</button>
              <input type="number" value={quantity} readOnly />
              <button onClick={() => handleQuantityChange("increase")}>+</button>
            </div>
            <button className="add-to-cart" onClick={handleAddToCart}>Add to cart</button>
            <button className="wishlist-btn">♡</button>
          </div>

          {/* Additional Details */}
          <p className="sku"><strong>SKU:</strong> {product.sku || "N/A"}</p>
          <p className="categories">
            <strong>Categories:</strong> {product.categories && product.categories.length > 0 ? product.categories.join(" | ") : "None"}
          </p>
          <p className="tags">
            <strong>Tags:</strong> {product.tags && product.tags.length > 0 ? product.tags.join(" | ") : "None"}
          </p>

          {/* Share Option */}
          <p className="share-items"><strong>Share this item:</strong></p>
        </div>
      </div>
    </div>
  );
};

export default ProductPopup;
