import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "./components/Header";
import ItemCard from "./components/ItemCard";
import ProductPopup from "./components/ProductPopup";
import CartSidebar from "./components/CartSidebar";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

const API_URL = "https://interview.gdev.gosbfy.com/api/collections/Products/records";
const IMAGE_URL_TEMPLATE = "https://interview.gdev.gosbfy.com/api/files";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [popupProduct, setPopupProduct] = useState(null);
  const [showCart, setShowCart] = useState(false);
  const [cartCount, setCartCount] = useState(0);
  const [wishlistCount, setWishlistCount] = useState(0);

  // Fetch products from the API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(API_URL);
        const data = await response.json();
        console.log("API Response:", data); // Debug API Response

        if (data && data.items) {
          const formattedProducts = data.items.map((item) => ({
            ...item,
            image: item.image
              ? `${IMAGE_URL_TEMPLATE}/${item.collectionId}/${item.id}/${item.image}`
              : "https://via.placeholder.com/300", // Fallback image
          }));

          setProducts(formattedProducts);
        } else {
          console.error("Invalid API response format:", data);
        }
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  // Handle clicking on a product to open the popup
  const handleProductClick = (product) => {
    console.log("Product clicked:", product); // Debugging
    setPopupProduct(product);
  };

  // Function to add products to cart with quantity handling
  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingProduct = prevCart.find((item) => item.id === product.id);

      if (existingProduct) {
        return prevCart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + product.quantity } : item
        );
      } else {
        return [...prevCart, { ...product }];
      }
    });

    setCartCount((prevCount) => prevCount + product.quantity);
  };

  return (
    <div>
      <Header cartCount={cart.reduce((total, item) => total + item.quantity, 0)} onCartClick={() => setShowCart(true)} />

      <div className="product-list d-flex flex-wrap gap-3 p-3">
        {products.length === 0 ? (
          <p>No products available. Please check the API.</p>
        ) : (
          products.map((product) => (
            <ItemCard key={product.id} product={product} onProductClick={handleProductClick} />
          ))
        )}
      </div>

      {popupProduct && (
        <ProductPopup
          key={popupProduct?.id} // Force re-render when a new product is clicked
          product={popupProduct}
          onClose={() => setPopupProduct(null)}
          onAddToCart={addToCart}
        />
      )}

      {showCart && <CartSidebar cartItems={cart} onClose={() => setShowCart(false)} />}
    </div>
  );
}

export default App;
