import React, { useState, useEffect } from "react";
import Header from "./components/Header";
import ProductList from "./components/ProductList";
import ProductPopup from "./components/ProductPopup";

const App = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Fetch products from API
  useEffect(() => {
    fetch("https://interview.gdev.gosbfy.com/api/collections/Products/rec")
      .then(response => response.json())
      .then(data => setProducts(data.items || []))
      .catch(error => console.error("Error fetching products:", error));
  }, []);

  // Function to add items to the cart
  const addToCart = (product, quantity) => {
    setCart(prevCart => {
      const existingItem = prevCart.find(item => item.id === product.id);

      if (existingItem) {
        return prevCart.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + quantity } : item
        );
      } else {
        return [...prevCart, { ...product, quantity }];
      }
    });

    setSelectedProduct(null); // Close popup after adding to cart
  };

  return (
    <div className="app">
      <Header cartCount={cart.reduce((total, item) => total + item.quantity, 0)} />
      <ProductList products={products} setSelectedProduct={setSelectedProduct} />

      {selectedProduct && (
        <ProductPopup 
          product={selectedProduct} 
          onClose={() => setSelectedProduct(null)} 
          addToCart={addToCart} 
        />
      )}
    </div>
  );
};

export default App;
