import React, { useState, useEffect } from "react";
import Header from "./components/Header";
import ItemCard from "./components/ItemCard";
import ProductPopup from "./components/ProductPopup";
import CartSidebar from "./components/CartSidebar";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

const API_URL = "https://interview.gdev.gosbfy.com/api/collections/Products/records";
const IMAGE_URL_TEMPLATE = "https://interview.gdev.gosbfy.com/api/files/";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [popupProduct, setPopupProduct] = useState(null);
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(API_URL);
        const data = await response.json();
        console.log("API Response:", data); // âœ… Debugging API response

        if (data && data.items) {
          setProducts(
            data.items.map((item) => ({
              id: item.id,
              name: item.Name || "Unnamed Product", // âœ… Fix: Ensure correct key
              description: item.Desc || "No description available", // âœ… Fix: Correct key
              price: item.Price || 0, // âœ… Fix: Ensure price is not undefined
              image: item.image
                ? `${IMAGE_URL_TEMPLATE}${item.collectionId}/${item.id}/${item.image}`
                : "https://via.placeholder.com/400", // âœ… Handle missing image
            }))
          );
        } else {
          console.error("Invalid API response format:", data);
        }
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

 const handleProductClick = (product) => {
  console.log("ðŸ”µ Product Clicked:", product); // âœ… Debugging
  setPopupProduct(product);
};
const addToCart = (product) => {
    if (!product) return;

    setCart((prevCart) => {
      const existingProduct = prevCart.find((item) => item.id === product.id);

      if (existingProduct) {
        return prevCart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });

    setCartCount((prevCount) => prevCount + 1);
  };

  return (
    <div>
      <Header cartCount={cart.reduce((total, item) => total + item.quantity, 0)} onCartClick={() => setShowCart(true)} />

      <div className="product-list d-flex flex-wrap gap-3 p-3">
        {products.length === 0 ? (
          <p>No products available. Please check the API.</p>
        ) : (
          products.map((product) => (
            <ItemCard key={product.id} product={product} onProductClick={handleProductClick} />
          ))
        )}
      </div>

      {popupProduct && (
       <ProductPopup
       key={popupProduct?.id}  // Ensure re-render on new product
       product={popupProduct}
       onClose={() => setPopupProduct(null)}
       onAddToCart={addToCart} // âœ… Make sure this is correctly passed
     />
      )}

      {showCart && <CartSidebar cartItems={cart} onClose={() => setShowCart(false)} />}
    </div>
  );
}

export default App;
