import React, { useState, useEffect } from "react";
import Header from "./components/Header";
import ItemCard from "./components/ItemCard";
import ProductPopup from "./components/ProductPopup";
import CartSidebar from "./components/CartSidebar";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

const API_URL = "https://interview.gdev.gosbfy.com/api/collections/Products/records";
const IMAGE_URL_TEMPLATE = "https://interview.gdev.gosbfy.com/api/files";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [popupProduct, setPopupProduct] = useState(null);
  const [showCart, setShowCart] = useState(false);

  // Fetch products from the API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch(API_URL);
        const data = await response.json();
        console.log("API Response:", data); // Debug API Response

        if (data && data.items) {
          setProducts(
            data.items.map((item) => ({
              ...item,
              image: item.image
                ? `${IMAGE_URL_TEMPLATE}/${item.collectionId}/${item.id}/${item.image}`
                : "/placeholder.jpg", // ðŸ”¥ Use a placeholder image if missing
            }))
          );
        } else {
          console.error("Invalid API response format:", data);
        }
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  // Function to add products to cart with quantity handling
  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingProduct = prevCart.find((item) => item.id === product.id);

      if (existingProduct) {
        // Update quantity if product already exists
        return prevCart.map((item) =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + (product.quantity || 1) } // ðŸ”¥ Default to 1 if missing
            : item
        );
      } else {
        // Add new product with default quantity of 1
        return [...prevCart, { ...product, quantity: product.quantity || 1 }];
      }
    });
  };

  // ðŸ”¥ Calculate cart count dynamically
  const cartCount = cart.reduce((total, item) => total + item.quantity, 0);

  return (
    <div>
      <Header cartCount={cartCount} onCartClick={() => setShowCart(true)} />

      <div className="product-list d-flex flex-wrap gap-3 p-3">
        {products.length === 0 ? (
          <p>No products available. Please check the API.</p>
        ) : (
          products.map((product) => (
            <ItemCard key={product.id} product={product} onProductClick={setPopupProduct} />
          ))
        )}
      </div>

      <ProductPopup
        product={popupProduct}
        onClose={() => setPopupProduct(null)}
        onAddToCart={addToCart}
      />

      {showCart && <CartSidebar cartItems={cart} onClose={() => setShowCart(false)} />}
    </div>
  );
}

export default App;
