import React from "react";
import "./CartSidebar.css";

const CartSidebar = ({ cartItems, onClose }) => {
  // Calculate subtotal
  const subtotal = cartItems.reduce((sum, item) => sum + (item.price || 0) * item.quantity, 0);

  return (
    <div className="cart-sidebar">
      <button className="close-btn" onClick={onClose}>âœ–</button>
      <h2>Shopping Cart</h2>

      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cartItems.map((item) => (
            <li key={item.id} className="cart-item">
              <img src={item.image || "https://via.placeholder.com/100"} alt={item.name || "No Name"} />
              <div>
                <p className="cart-item-name">{item.name || "No Name"}</p>
                <p className="cart-item-price">${item.price?.toFixed(2) || "0.00"}</p>
                <p>Quantity: {item.quantity}</p>
              </div>
            </li>
          ))}
        </ul>
      )}

      <div className="cart-footer">
        <p><strong>Subtotal:</strong> ${subtotal.toFixed(2)}</p>
      </div>
    </div>
  );
};

export default CartSidebar;
