import React, { useEffect } from "react";

const ProductPopup = ({ product, onClose, onAddToCart }) => {
  useEffect(() => {
    console.log("Popup opened for:", product); // Debugging info
  }, [product]);

  // ✅ Prevents errors if product is null or undefined
  if (!product) return null;

  // ✅ Check if required properties exist before using them
  const imageUrl =
    product.collectionId && product.id && product.image
      ? `https://interview.gdev.gosbfy.com/api/files/${product.collectionId}/${product.id}/${product.image}`
      : null;

  return (
    <div className="popup-overlay">
      <div className="popup-content">
        <button className="close-btn" onClick={onClose}>×</button>

        {/* ✅ Only render the image if the URL is valid */}
        {imageUrl ? (
          <img
            key={imageUrl} // Forces re-render when image changes
            src={imageUrl}
            alt={product.name || "Product Image"}
            className="popup-image"
            onError={(e) => {
              console.error("Image failed to load:", imageUrl);
              e.target.src = "/fallback-image.jpg"; // Optional: Set a fallback image
            }}
          />
        ) : (
          <p>No image available</p>
        )}

        {/* ✅ Fix property names (match your API response) */}
        <h2>{product.name || "Unnamed Product"}</h2>
        <p>Price: ${product.price || "N/A"}</p>

        <button className="add-to-cart" onClick={() => onAddToCart(product)}>
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default ProductPopup;
