import React, { useEffect, useState } from "react";
import "./ProductPopup.css";

const ProductPopup = ({ product, onClose, onAddToCart }) => {
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    console.log("Popup Product:", product); // Debugging
  }, [product]);

  if (!product) return null; // Prevents errors if no product is selected

  const imageUrl = product.image
    ? `https://interview.gdev.gosbfy.com/api/files/${product.collectionId}/${product.id}/${product.image}`
    : "https://via.placeholder.com/300"; // Fallback image

  const price = product.price ? product.price.toFixed(2) : "0.00"; // Ensure price is valid

  return (
    <div className="popup-overlay">
      <div className="popup-content">
        <button className="close-btn" onClick={onClose}>Ã—</button>

        {/* Product Image */}
        <img src={imageUrl} alt={product.name || "Product"} className="popup-image" />

        {/* Product Details */}
        <h2>{product.name || "No Name"}</h2>
        <p>${price}</p>
        <p><strong>Available:</strong> <span className="stock-status">In stock</span></p>
        <p>{product.description || "No description available."}</p>

        {/* Quantity Selector */}
        <div className="quantity-container">
          <button onClick={() => setQuantity((q) => Math.max(1, q - 1))}>-</button>
          <input type="number" value={quantity} readOnly />
          <button onClick={() => setQuantity((q) => q + 1)}>+</button>
        </div>

        {/* Add to Cart Button */}
        <button className="add-to-cart" onClick={() => onAddToCart({ ...product, quantity })}>
          Add to cart
        </button>
      </div>
    </div>
  );
};

export default ProductPopup;
