import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "./components/Header";
import ItemCard from "./components/ItemCard";
import ProductPopup from "./components/ProductPopup";
import CartSidebar from "./components/CartSidebar";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

const API_URL = "https://interview.gdev.gosbfy.com/api/collections/Products/records";
const IMAGE_URL_TEMPLATE = "https://interview.gdev.gosbfy.com/api/files/";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [popupProduct, setPopupProduct] = useState(null);
  const [showCart, setShowCart] = useState(false); // Controls cart sidebar visibility

  // Fetch products from API
  useEffect(() => {
  const fetchProducts = async () => {
    try {
      const response = await axios.get(API_URL);
      console.log("Raw API Response:", response.data); // Debug API response

      if (response.data && response.data.items) {
        const formattedProducts = response.data.items.map((item) => ({
          id: item.id,
          name: item.name || "Unknown Product",  // Ensure name exists
          price: item.price || 0,  // Ensure price exists
          image: item.image 
            ? `${IMAGE_URL_TEMPLATE}${item.collectionId}/${item.id}/${item.image}` 
            : "https://via.placeholder.com/300", // Default image
        }));

        console.log("Formatted Products:", formattedProducts); // Debug formatted data
        setProducts(formattedProducts);
      } else {
        console.error("Invalid API response format:", response.data);
      }
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  fetchProducts();
}, []);


  // Function to add products to the cart
  const addToCart = (product) => {
    console.log("Product being added:", product); // Check product data before adding

    if (!product || !product.name || !product.price) {
      console.error("Invalid product data:", product);
      return;
    }

    setCart((prevCart) => {
      const existingProduct = prevCart.find((item) => item.id === product.id);

      if (existingProduct) {
        return prevCart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });

    console.log("Updated Cart:", cart); // Debug cart content
  };

  const handleProductClick = (product) => {
    console.log("Product Clicked:", product);
    setPopupProduct(product);
  };

  return (
    <div>
      {/* Header with Cart Button */}
      <Header 
        cartCount={cart.reduce((total, item) => total + item.quantity, 0)} 
        onCartClick={() => setShowCart(true)} 
      />

      {/* Product List */}
      <div className="product-list d-flex flex-wrap gap-3 p-3">
        {products.length === 0 ? (
          <p>No products available. Please check the API.</p>
        ) : (
          products.map((product) => (
            <ItemCard key={product.id} product={product} onProductClick={handleProductClick} />
          ))
        )}
      </div>

      {/* Product Popup */}
      {popupProduct && (
        <ProductPopup product={popupProduct} onClose={() => setPopupProduct(null)} onAddToCart={addToCart} />
      )}

      {/* Cart Sidebar (Visibility controlled by showCart state) */}
      {showCart && (
        <div className="cart-sidebar open">
          <CartSidebar cartItems={cart} onClose={() => setShowCart(false)} />
        </div>
      )}
    </div>
  );
}

export default App;
