import React, { useState } from "react";

const ProductPopup = ({ product, onClose, addToCart }) => {
  const [quantity, setQuantity] = useState(1);

  // Function to handle increasing the quantity
  const increaseQuantity = () => setQuantity(quantity + 1);

  // Function to handle decreasing the quantity (minimum 1)
  const decreaseQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  return (
    <div className="popup-overlay">
      <div className="popup-content">
        <button className="close-btn" onClick={onClose}>Ã—</button>
        
        {/* Product Image */}
        <img 
          src={`https://interview.gdev.gosbfy.com/api/files/${product.collectionId}/${product.id}/${product.image}`} 
          alt={product.Name} 
          className="product-image" 
        />

        {/* Product Details */}
        <div className="product-details">
          <h2>{product.Name}</h2>
          <p className="price">${product.Price.toFixed(2)}</p>
          <p><strong>Available:</strong> <span className="in-stock">in-stock</span></p>
          <p>{product.Desc}</p>

          {/* Quantity Selector */}
          <div className="quantity-container">
            <button className="quantity-btn" onClick={decreaseQuantity}>-</button>
            <span className="quantity">{quantity}</span>
            <button className="quantity-btn" onClick={increaseQuantity}>+</button>
          </div>

          {/* Add to Cart Button */}
          <button className="add-to-cart-btn" onClick={() => addToCart(product, quantity)}>
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductPopup;
