import React from "react";
import "./ProductPopup.css";

const ProductPopup = ({ product, onClose, onAddToCart }) => {
  if (!product) return null; // Prevents crashes if product is null

  const imageUrl = `https://interview.gdev.gosbfy.com/api/files/${product.collectionId}/${product.id}/${product.image}`;

  return (
    <div className="popup-overlay">
      <div className="popup-content">
        <button className="close-btn" onClick={onClose}>Ã—</button>
        
        {/* Product Image */}
        {product.image ? (
          <img src={imageUrl} alt={product.Name} className="popup-image" />
        ) : (
          <p>No Image Available</p>
        )}

        {/* Product Details */}
        <h2>{product.Name || "No Name Available"}</h2>
        <p><strong>Price:</strong> ${product.Price || "N/A"}</p>
        <p className="stock-status">{product.stock ? "In Stock" : "Out of Stock"}</p>

        {/* Add to Cart */}
        <button className="add-to-cart" onClick={() => onAddToCart(product)}>
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default ProductPopup;
