import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "./components/Header";
import ItemCard from "./components/ItemCard";
import ProductPopup from "./components/ProductPopup";
import CartSidebar from "./components/CartSidebar";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

const API_URL = "https://interview.gdev.gosbfy.com/api/collections/Products/rec";
const IMAGE_URL_TEMPLATE = "https://interview.gdev.gosbfy.com/api/files";

function App() {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [popupProduct, setPopupProduct] = useState(null);
  const [showCart, setShowCart] = useState(false);

  // Fetch products from the API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get(API_URL);
        console.log("API Response:", response.data); // Debugging log
        const fetchedProducts = response.data.items.map((item) => ({
          ...item,
          image: `${IMAGE_URL_TEMPLATE}/${item.collectionId}/${item.id}/${item.image}`,
        }));
        setProducts(fetchedProducts);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  return (
    <div>
      <Header cartCount={cart.length} onCartClick={() => setShowCart(true)} />
      <div className="product-list d-flex flex-wrap gap-3 p-3">
        {products.map((product) => (
          <ItemCard
            key={product.id}
            product={product}
            onProductClick={setPopupProduct}
          />
        ))}
      </div>
      <ProductPopup
        product={popupProduct}
        onClose={() => setPopupProduct(null)}
        onAddToCart={addToCart}
      />
      {showCart && <CartSidebar cartItems={cart} onClose={() => setShowCart(false)} />}
    </div>
  );
}

export default App;
