import React from "react";
import "./ProductPopup.css";

const ProductPopup = ({ product, onClose, onAddToCart }) => {
  if (!product) return null; // Prevents crashes

  // ðŸ”´ Debugging: Check if product data is correctly received
  console.log("Popup Product Data:", product);

  // Construct the image URL properly
  const imageUrl = product.image
    ? `https://interview.gdev.gosbfy.com/api/files/${product.collectionId}/${product.id}/${product.image}`
    : null;

  return (
    <div className="popup-overlay">
      <div className="popup-content">
        <button className="close-btn" onClick={onClose}>Ã—</button>

        {/* Show product image if available */}
        {imageUrl ? (
          <img src={imageUrl} alt={product.Name} className="popup-image" onError={(e) => e.target.style.display = 'none'} />
        ) : (
          <p>No Image Available</p>
        )}

        <h2>{product.Name || "No Name Available"}</h2>
        <p><strong>Price:</strong> ${product.Price || "N/A"}</p>

        <button className="add-to-cart" onClick={() => onAddToCart(product)}>Add to Cart</button>
      </div>
    </div>
  );
};

export default ProductPopup;
