import React from "react";
import { FaUser, FaHeart, FaShoppingBag, FaSearch, FaBars } from "react-icons/fa";
import "./Header.css";

const Header = ({ cart = [], wishlistCount = 0 }) => {
  // Ensure cart is always an array before using reduce
  const totalCartItems = cart.reduce((total, item) => total + (item.quantity || 1), 0);

  return (
    <header className="header">
      {/* Left Section - Search Bar */}
      <div className="search-container">
        <input type="text" placeholder="Search Anything..." className="search-input" />
        <FaSearch className="search-icon" />
      </div>

      {/* Center Section - Logo */}
      <h1 className="logo">Helendo</h1>

      {/* Right Section - Profile, Wishlist, Cart, and Menu */}
      <div className="header-icons">
        <FaUser className="icon" />

        {/* Wishlist Icon with Badge */}
        <div className="icon-container">
          <FaHeart className="icon" />
          {wishlistCount > 0 && <span className="badge">{wishlistCount}</span>}
        </div>

        {/* Cart Icon with Total Items Count */}
        <div className="icon-container">
          <FaShoppingBag className="icon" />
          {totalCartItems > 0 && <span className="badge">{totalCartItems}</span>}
        </div>

        {/* Menu Icon */}
        <FaBars className="icon" />
      </div>
    </header>
  );
};

export default Header;
