import React from "react";
//import "./ProductPopup.css";

const ProductPopup = ({ product, onClose, onAddToCart }) => {
  // If product is null, return nothing to avoid errors
  if (!product) return null;

  return (
    <div className="popup-overlay">
      <div className="popup-content">
        <button className="close-btn" onClick={onClose}>Ã—</button>

        {/* Product Image */}
        <img
          src={`https://interview.gdev.gosbfy.com/api/files/${product.collectionId}/${product.id}/${product.image}`}
          alt={product.name}
          className="popup-image"
        />

        {/* Product Details */}
        <h2>{product.name}</h2>
        <p>${product.price}</p>
        <p><strong>Available:</strong> <span className="stock-status">In stock</span></p>
        <p>{product.description}</p>

        {/* Quantity Selector and Add to Cart */}
        <div className="quantity-container">
          <button>-</button>
          <input type="number" value={1} readOnly />
          <button>+</button>
          <button className="add-to-cart" onClick={() => onAddToCart(product)}>Add to cart</button>
        </div>
      </div>
    </div>
  );
};

export default ProductPopup;
