import React from "react";

function ProductPopup({ product, onClose, onAddToCart }) {
  if (!product) return null;

  return (
    <div className="popup">
      <div className="popup-content">
        <button className="close-btn" onClick={onClose}>âœ–</button>
        <img 
          src={product.image || "https://via.placeholder.com/300"} 
          alt={product.name || "No Name"} 
          className="popup-image"
        />
        <h2>{product.name || "No Name"}</h2>
        <p className="price">${product.price ? product.price.toFixed(2) : "0.00"}</p>
        <p>{product.description || "No description available."}</p>
        <p><strong>SKU:</strong> {product.sku || "N/A"}</p>
        <p><strong>Categories:</strong> {product.categories?.length ? product.categories.join(", ") : "None"}</p>
        <p><strong>Tags:</strong> {product.tags?.length ? product.tags.join(", ") : "None"}</p>
        <button className="add-to-cart-btn" onClick={() => onAddToCart(product)}>
          Add to Cart
        </button>
      </div>
    </div>
  );
}

export default ProductPopup;
