import React, { useState } from "react";
import ProductPopup from "./ProductPopup";
import CartSidebar from "./CartSidebar";
import "./App.css"; // Add styles for cart sidebar

const App = () => {
  const [cart, setCart] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [isCartOpen, setIsCartOpen] = useState(false); // Cart visibility state

  // Add to Cart function
  const addToCart = (product) => {
    if (!product) return;

    setCart((prevCart) => {
      const existingProduct = prevCart.find((item) => item.id === product.id);
      if (existingProduct) {
        return prevCart.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });

    setIsCartOpen(true); // Open cart sidebar after adding a product
  };

  const handleProductClick = (product) => {
    setSelectedProduct(product);
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setSelectedProduct(null);
    setIsPopupOpen(false);
  };

  const toggleCart = () => {
    setIsCartOpen((prev) => !prev);
  };

  return (
    <div>
      {/* ðŸ”¹ HEADER - View Cart Button */}
      <header className="header">
        <button onClick={toggleCart} className="cart-button">
          ðŸ›’ View Cart ({cart.length})
        </button>
      </header>

      {/* ðŸ”¹ Product Popup */}
      {isPopupOpen && (
        <ProductPopup product={selectedProduct} onClose={closePopup} onAddToCart={addToCart} />
      )}

      {/* ðŸ”¹ Cart Sidebar */}
      <CartSidebar cartItems={cart} onClose={toggleCart} isOpen={isCartOpen} />
    </div>
  );
};

export default App;
