import React from "react";
function Cart({ cartItems, onClose }) {
  return (
    <div className="cart-popup bg-light p-3 border rounded" style={{ position: 'fixed', top: 80, right: 20, zIndex: 999 }}>
      <h5>Shopping Cart</h5>
      {cartItems.length === 0 ? (
        <p>No items in cart</p>
      ) : (
        <ul className="list-unstyled">
          {cartItems.map((item) => (
            <li key={item.id} className="mb-2">
              <strong>{item.title}</strong> x {item.quantity}
            </li>
          ))}
        </ul>
      )}
      <button className="btn btn-danger btn-sm" onClick={onClose}>Close</button>
    </div>
  );
}

export default Cart;
