import React, { useState } from "react";
import "./ProductPopup.css";

const ProductPopup = ({ product, onClose, onAddToCart }) => {
  // Ensure hooks are at the top
  const [quantity, setQuantity] = useState(1);

  // Return early if no product (but after useState)
  if (!product) return null;

  const handleQuantityChange = (type) => {
    setQuantity((prevQuantity) => 
      type === "increase" ? prevQuantity + 1 : Math.max(1, prevQuantity - 1)
    );
  };

  const handleAddToCart = () => {
    onAddToCart({ ...product, quantity });
    onClose(); // Close popup automatically after adding to cart
  };

  return (
    <div className="popup-overlay">
      <div className="popup-content">
        <button className="close-btn" onClick={onClose}>Ã—</button>

        {/* Product Image */}
        {product.image && (
          <img
            src={`https://interview.gdev.gosbfy.com/api/files`}
            alt={product.name}
            className="popup-image"
          />
        )}

        {/* Product Details */}
        <h2>{product.name}</h2>
        <p>${product.price}</p>
        <p><strong>Available:</strong> <span className="stock-status">In stock</span></p>
        <p>{product.description}</p>

        {/* Quantity Selector */}
        <div className="quantity-container">
          <button onClick={() => handleQuantityChange("decrease")}>-</button>
          <input type="number" value={quantity} readOnly />
          <button onClick={() => handleQuantityChange("increase")}>+</button>
        </div>

        {/* Add to Cart Button */}
        <button className="add-to-cart" onClick={handleAddToCart}>
          Add to cart
        </button>
      </div>
    </div>
  );
};

export default ProductPopup;
